import React, { useState } from "react";


const itemsData = [
  { id: 1, name: "SHOES", price: "₹1059", info: "Lorem ipsum dolor sit amet consectetur.", img: "https://images.pexels.com/photos/1464625/pexels-photo-1464625.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
  { id: 2, name: "MEN's T-SHIRT", price: "₹879", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/3649765/pexels-photo-3649765.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
  { id: 3, name: "JEANS", price: "₹999", info: "Lorem ipsum dolor sit amet.", img: "https://i.postimg.cc/Nj7KFXtz/calvin-klein-jeans-pure-blue-mens-slim-straight-pure-blue-jeans-product-3-066028341-normal.webp" },
  { id: 4, name: "WATCH", price: "₹1099", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/8839887/pexels-photo-8839887.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
  { id: 5, name: "SMART PHONE", price: "₹80,000", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/6858618/pexels-photo-6858618.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
  { id: 6, name: "TELEVISION", price: "₹18,000", info: "Lorem ipsum dolor sit amet consectetur.", img: "https://images.pexels.com/photos/5552789/pexels-photo-5552789.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
  { id: 7, name: "HOODIES", price: "₹759", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/4295985/pexels-photo-4295985.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
  { id: 8, name: "DINNER SET", price: "₹1049", info: "Lorem ipsum dolor sit amet consectetur adipisicing elit.", img: "https://i.postimg.cc/qvdwhWhM/OIP.jpg" },
  { id: 9, name: "BLANKETS", price: "₹599", info: "Lorem ipsum dolor sit amet consectetur adipisicing.", img: "https://images.pexels.com/photos/6463348/pexels-photo-6463348.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
  { id: 10, name: "LAPTOP", price: "₹62,500", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/2659939/pexels-photo-2659939.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
];

const OurProducts = () => {
  const [searchTerm, setSearchTerm] = useState("");

  const filteredItems = itemsData.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="shop-container">
      <h1>Shop</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search products"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="items-grid">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <div key={item.id} className="item-card">
              <img src={item.img} alt={item.name} className="item-image" />
              <h2>{item.name}</h2>
              <p>{item.info}</p>
              <p className="price">{item.price}</p>
            </div>
          ))
        ) : (
          <p className="no-results">No products found</p>
        )}
      </div>
    </div>
  );
};

export default OurProducts;
