import React from 'react';
import '../pages/Shop.css';

document.addEventListener("DOMContentLoaded", () => {
  const itemsData = [
      { name: "SHOES", price: "₹1059", info: "Lorem ipsum dolor sit amet consectetur.", img: "https://images.pexels.com/photos/1464625/pexels-photo-1464625.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "MEN's T-SHIRT", price: "₹879", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/3649765/pexels-photo-3649765.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
      { name: "JEANS", price: "₹999", info: "Lorem ipsum dolor sit amet.", img: "https://media.istockphoto.com/photos/folded-blue-jeans-on-a-white-background-modern-casual-clothing-flat-picture-id1281304280" },
      { name: "WATCH", price: "₹1099", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/8839887/pexels-photo-8839887.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "SMART PHONE", price: "₹80,000", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/6858618/pexels-photo-6858618.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "TELEVISION", price: "₹18,000", info: "Lorem ipsum dolor sit amet consectetur.", img: "https://images.pexels.com/photos/5552789/pexels-photo-5552789.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "HOODIES", price: "₹759", info: "Lorem ipsum dolor sit.", img: "https://images.pexels.com/photos/4295985/pexels-photo-4295985.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
      { name: "DINNER SET", price: "₹1049", info: "Lorem ipsum dolor sit amet consectetur adipisicing elit.", img: "https://media.istockphoto.com/photos/vintage-plates-with-silver-teaspoons-picture-id184363070" },
      { name: "BLANKETS", price: "₹599", info: "Lorem ipsum dolor sit amet consectetur adipisicing.", img: "https://images.pexels.com/photos/6463348/pexels-photo-6463348.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
      { name: "LAPTOP", price: "₹62,500", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/2659939/pexels-photo-2659939.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
      { name: "MICROWAVE", price: "₹78,999", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://media.istockphoto.com/photos/modern-kitchen-microwave-oven-picture-id1144960519" },
      { name: "COFFEE MAKER", price: "₹29,999", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://media.istockphoto.com/photos/black-coffee-maker-with-green-led-lights-picture-id177395430" },
      { name: "BED", price: "₹39,949", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/6606354/pexels-photo-6606354.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" },
      { name: "AIR CONDITIONER", price: "₹59,449", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://media.istockphoto.com/photos/woman-turning-on-air-conditioner-picture-id1325708905" },
      { name: "BOOK", price: "₹259", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/5834/nature-grass-leaf-green.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "BAG", price: "₹439", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/4339598/pexels-photo-4339598.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" },
      { name: "SAREES", price: "₹1049", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://media.istockphoto.com/photos/hand-of-a-lady-selecting-yellow-colored-saree-in-a-shop-picture-id1301740530" },
      { name: "WASHING MACHINE", price: "₹56,000", info: "Lorem ipsum dolor sit amet consectetur, adipisicing elit.", img: "https://images.pexels.com/photos/5816934/pexels-photo-5816934.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" }
  ];

  const container = document.querySelector(".container");

  itemsData.forEach(item => {
      const itemElement = document.createElement("div");
      itemElement.classList.add("items");

      itemElement.innerHTML = `
          <div class="img">
              <img src="${item.img}" alt="${item.name}" />
          </div>
          <div class="name">${item.name}</div>
          <div class="price">${item.price}</div>
          <div class="info">${item.info}</div>
      `;

      container.appendChild(itemElement);
  });
});

const Shop = () => {
  return (
    <div className="shop-container">
      <h1>Shop Page</h1>
      <div className="items-grid">
        {items.map(item => (
          <div key={item.id} className="item-card">
            <h2>{item.name}</h2>
            <p>{item.description}</p>
            <p className="price">{item.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop
